import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { Coffee } from './Coffee';

@Injectable({
  providedIn: 'root'
})
export class CoffeeService {

  private coffeeUrl = 'https://gist.githubusercontent.com/josejbocanegra/e9d24db370ce95b75555f7d1f8691805/raw/8a26ac2bca4183dc88545e14c45851d698911358/202212_MISW4104_Grupo3.json';

  constructor(private http: HttpClient) { }

  getCoffees(): Observable<Coffee[]> {
    return this.http.get<Coffee[]>(this.coffeeUrl).pipe(
      map((coffees: any[]) =>
        coffees.map(
          (coffee: any) =>
            new Coffee(
              coffee.id,
              coffee.nombre,
              coffee.tipo,
              coffee.region,
              coffee.sabor,
              coffee.altura,
              coffee.imagen
            )
        )
      )
    );
  }

}
